source $stdenv/setup

shopt -s nullglob

modfile="$out/modules/$modName"
mkdir -p `dirname "$modfile"`

# http://modules.sourceforge.net/man/modulefile.html
cat > $modfile <<EOF
-- Autogenerated nix $name
help([[
    Sets the environment for nix-built $pkgName-$pkgVersion
    Autogenerated for nix package $buildInputs
]])
whatis("Name: $pkgName")
whatis("Version: $pkgVersion")
whatis("Description: Sets the environment for nix-built $pkgName-$pkgVersion")
EOF

if [[ $modLoad ]] ; then
  for m in $modLoad; do
    echo "load(\"$m\")" >> $modfile
    echo "prereq(\"$m\")" >> $modfile
  done
fi
if [[ $modPrereq ]] ; then
    echo "prereq(\"$modPrereq\")" >> $modfile
fi
if [[ $modConflict ]] ; then
  for m in $modConflict; do
    echo "conflict(\"$m\")" >> $modfile
  done
fi

addList () {
	if [[ -e $1 ]] ; then
		for p in `< $1` ; do
			addPaths $p
		done
	fi
}

declare -A done
addPaths () {
	if [[ ${done[$1]} ]] ; then
		return
	fi
	done[$1]=1

	addList $1/nix-support/propagated-user-env-packages
	addList $1/nix-support/propagated-build-inputs

	if [[ -d $1/bin ]] ; then
		echo "prepend_path(\"PATH\", \"$1/bin\")" >> $modfile
	fi
	if [[ -d $1/share/man ]] ; then
		echo "prepend_path(\"MANPATH\", \"$1/share/man\")" >> $modfile
	fi
	if [[ -d $1/lib/pkgconfig ]] ; then
		echo "prepend_path(\"PKG_CONFIG_PATH\", \"$1/lib/pkgconfig\")" >> $modfile
	fi
	if [[ -d $1/share/pkgconfig ]] ; then
		echo "prepend_path(\"PKG_CONFIG_PATH\", \"$1/share/pkgconfig\")" >> $modfile
	fi
	if [[ -d $1/lib/perl5/site_perl ]] ; then
		echo "prepend_path(\"PERL5LIB\", \"$1/lib/perl5/site_perl\")" >> $modfile
	fi
    pythonpath=($1/lib/python*/site-packages)
	if [[ -n $pythonpath ]] ; then
		echo "prepend_path(\"PYTHONPATH\", \"$pythonpath\")" >> $modfile
	fi
	libs=($1/lib/lib*.so)
	if [[ $addLDLibraryPath && -n $libs ]] ; then
		echo "prepend_path(\"LD_LIBRARY_PATH\", \"$1/lib\")" >> $modfile
	fi
	if [[ $addCFlags && ( -d $1/include || -n $libs ) ]] ; then
		if [[ -z $gccPrereq ]] ; then
			#echo "prereq ${modPrefix}gcc" >> $modfile
			gccPrereq=1
		fi
		if [[ -d $1/include ]] ; then
			# nix uses -isystem but we'll just use -I
			echo "prepend_path(\"NIX_${nixInfix}_CFLAGS_COMPILE\", \"-I$1/include\", \" \")" >> $modfile
		fi
		if [[ -n $libs ]] ; then
			echo "prepend_path(\"NIX_${nixInfix}_LDFLAGS\", \"-L$1/lib\", \" \")" >> $modfile
		fi
	fi
}

echo "setenv(\"${modEnv}_BASE\", \"$buildInputs\")" >> $modfile
echo "setenv(\"${modEnv}_VERSION\", \"$pkgVersion\")" >> $modfile
for root in $buildInputs ; do
	addPaths $root
done

echo "$pkgName modPath: $modPath"
if [[ $modPath ]] ; then
    echo "-- Setup Modulepath for packages built using this package (compiler/mpi/...)" >> $modfile
    echo "prepend_path(\"MODULEPATH\", \"$modPath\")" >> $modfile
fi
